INSERT INTO `webnhac_baihat` (`idBH`, `TenBH`, `LoiBH`, `urlBH`, `idCS`, `NgayCapNhat`, `SoLanNghe`, `idNS`, `idTL`, `SoLanDown`) VALUES
(1, 'Tiếng Gà Trưa', 'Về trong phố xưa tôi nằm <br />\r\nCó lần nghe tiếng ru bên vườn <br />\r\nChợt như xác thân không còn <br />\r\nVà cạnh tôi là đồng vắng <br />\r\n<br />\r\nVề trên phố cao nguyên ngồi <br />\r\nTiếng gà trưa gáy khan   bên đồi <br />\r\nChợt như phố kia không người <br />\r\nCòn lại tôi bước   hoài <br />\r\n<br />\r\nLòng ta có khi tựa như vắng ai <br />\r\nNhiều khi đã   vui cười nhiều khi đứng riêng ngoài <br />\r\nNhiều đêm muốn đi về con phố xa <br />\r\nNhiều đêm muốn quay về ngồi yên dưới mái nhà <br />\r\n<br />\r\nGiòng   sông trước kia tôi về <br />\r\nBỗng giờ đây đã khô không ngờ <br />\r\nLòng tôi có khi mơ hồ <br />\r\nTưởng mình đang là cơn gió <br />\r\n<br />\r\nVề chân núi thăm nấm mồ <br />\r\nGiữa đường trưa có tôi bơ   phờ <br />\r\nChợt tôi thấy thiên thu <br />\r\nLà một đường không bến bờ <br />\r\n', '2/TiengGaTrua.mp3', 4, '2008-07-01', 58, 2, 2, 110),
(2, 'Biển nhớ', '<p>Ngày mai em đi, biển nhớ tên em gọi về <br />\r\n  Gọi hồn liễu rủ lê   thê, gọi bờ cát trắng đêm khuya <br />\r\n  Ngày mai em đi, đồi núi nghiêng nghiêng đợi   chờ<br />\r\n  Sỏi đá trông em từng giờ, nghe buồn nhịp chân bơ vơ <br />\r\n  Ngày mai em đi,   biển nhớ em quay về nguồn <br />\r\n  Gọi trùng dương gió ngập hồn, bàn tay chắn gió mưa   sang <br />\r\n  Ngày mai em đi, thành phố mắt đêm đêm mờ <br />\r\n  Hồn lẽ nghiêng vai gọi   buồn, nghe ngoài biển động buồn hơn <br />\r\n  <br />\r\n  Hôm nào em về, bàn tay buông lối ngõ <br />\r\n  Đàn lên cung phím chờ sầu lên dây hoang vu <br />\r\n  Ngày mai em đi, biển nhớ tên   em gọi về <br />\r\n  Chiều sương ướt đẫm cơn mê <br />\r\n  Trời cao níu bước sơn khê <br />\r\n  <br />\r\n  Ngày mai em đi, cồn đá rêu phong rũ buồn <br />\r\n  Đèn phố nghe mưa tủi hồn,   nghe ngoài trời giăng mây luôn <br />\r\n  Ngày mai em đi, biển có bâng khuâng gọi thầm <br />\r\n  Ngày mưa tháng nắng còn buồn, bàn tay nghe ngóng tin sang <br />\r\n  Ngày mai em   đi, thành phố mắt đêm đèn vàng <br />\r\n  Nửa bóng xuân qua ngập ngừng, nghe trời gió   lộng mà thương </p>\r\n', '', 0, '2008-07-08', 4, 2, 2, 6),
(3, 'Cát bụi', 'Hạt bụi nào hóa kiếp thân tôi<br />\r\nĐể một mai vươn hình hài lớn dậy<br />\r\nÔi cát bụi   tuyệt vời!<br />\r\nMặt trời soi một kiếp rong chơi.<br />\r\nHạt bụi nào hóa kiếp thân   tôi,<br />\r\nĐể một mai tôi về làm cát bụi.<br />\r\nÔi cát bụi mệt nhoài!<br />\r\nTiếng động   nào gõ nhịp không nguôi.<br />\r\n<br />\r\nBao nhiêu năm làm kiếp con người,<br />\r\nChợt một   chiều tóc trắng như vôi<br />\r\nLá úa trên cao rụng đầy<br />\r\nCho trăm năm vào chết một   ngày.<br />\r\n<br />\r\nMặt trời nào soi sáng tim tôi<br />\r\nĐể tình yêu xay mòn thành đá   cuội<br />\r\nXin úp mặt bùi ngùi<br />\r\nTừng ngày qua mỏi ngóng tin vui.<br />\r\nCụm rừng nào   lá xác xơ cây<br />\r\nTừ vực sâu nghe lời mời đã dậy<br />\r\nÔi cát bụi phận này<br />\r\nVết   mực nào xóa bỏ không hay.<br />\r\n', '2/CatBui.mp3', 4, '2008-09-01', 3, 2, 2, 9),
(4, 'Còn ai với ai', '<p>Không có em còn tôi với ai <br />\r\n  Không có em lạnh giá đường vui <br />\r\n  Không có em ngồi đứng nơi này <br />\r\n  Không có em còn ai có ai <br />\r\n  Em đã đi chìm   khuất đã theo, em đã như ngọn gió quạnh hiu <br />\r\n  Không có em đường cũ tiêu điều   em đã xa lìa trong nỗi đau <br />\r\n  Em đi biền biệt muôn trùng quá <br />\r\n  Từng cơn gió   và từng cơn gió <br />\r\n  Em đi gió lạnh bến xa bờ <br />\r\n  Từng nỗi nhớ trùng trùng nỗi   nhớ <br />\r\n  ... À ...a... ơi ...í .. a <br />\r\n  Không có em đường xa quá xa <br />\r\n  Em đã   thôi cười giữa chiều mưa <br />\r\n  Em đã đi đời có đâu ngờ , mang trái tim mùa xuân   héo khô <br />\r\n  Không có em buồn vui với ai <br />\r\n  Không có em lụa gấm nhạt phai <br />\r\n  Ai   đã chia người mãi xa người <br />\r\n  Ai giết đi tình đang lứa đôi </p>\r\n', '', 0, '2008-07-09', 1, 2, 2, 4),
(5, 'Diễm xưa', '<p>Mưa vẫn bay bay trên tầng tháp cổ <br />\r\n  Dài tay em mấy thuở mắt   xanh xao <br />\r\n  Nghe lá thu mưa reo mòn gót nhỏ <br />\r\n  Đường dài hun hút cho mắt thêm   sâu<br />\r\n  Mưa vẫn hay mưa trên hàng lá nhỏ <br />\r\n  Buổi chiều ngồi ngóng những chuyến   mưa qua <br />\r\n  Trên bước chân em âm thầm lá đổ <br />\r\n  Chợt hồn xanh buốt cho mình xót   xa <br />\r\n  Chiều nay còn mưa sao em không lại <br />\r\n  Nhớ mãi trong cơn đau vùi <br />\r\n  Làm   sao có nhau hằn lên nỗi đau <br />\r\n  Bước chân em xin về mau <br />\r\n  Mưa vẫn hay mưa cho   đời biến động <br />\r\n  Làm sao em biết bia đá không đau<br />\r\n  Xin hãy cho mưa qua miền   đất rộng <br />\r\n  Ngày sau sỏi đá cũng cần có nhau<br />\r\n  Mưa vẫn hay mưa cho đời biến   động <br />\r\n  Làm sao em nhớ những vết chim di<br />\r\n  Xin hãy cho mưa qua miền đất   rộng <br />\r\n  Để người phiêu lãng quên mình lãng du</p>\r\n', '2/DiemXua.mp3', 4, '2008-07-08', 5, 2, 2, 0),
(6, 'Để gió cuốn đi', 'Sống trong đời sống cần có một tấm lòng <br />\r\nĐể làm gì em biết không ?<br />\r\nĐể gió   cuốn đi , để gió cuốn đi <br />\r\nGió cuốn đi cho mây qua dòng sông <br />\r\nNgày vừa lên   hay đêm xuống mêng mông <br />\r\nÔi trái tim đang bay theo thời gian <br />\r\nLàm chiếc   bóng đi rao lời dối gian<br />\r\n \r\n<br />\r\nNhững khi chiều tới cần có một tiếng cười <br />\r\nĐể ngậm ngùi theo lá bay <br />\r\nRồi nước cuốn trôi , rồi nước cuốn trôi <br />\r\n<br />\r\nHãy nghiêng đời xuống nhìn hết một mối tình<br />\r\nChỉ lặng nhìn không nói   năng <br />\r\nĐể buốt trái tim để buốt trái tim<br />\r\nTrong trái tim con chim đau nằm   yên <br />\r\nNgủ dài lâu mang theo vết thương sâu <br />\r\nVà sớm mai chim bay đi triền   miên <br />\r\nVà tiếng hót vang trong trời gió lên <br />\r\n<br />\r\nHãy yêu ngày tới dù quá   mệt kiếp người <br />\r\nCòn cuộc đời ta cứ vui <br />\r\nDù vắng bóng ai , dù vắng bóng ai\r\n', '2/DeGioCuonDi-TruongThanh.mp3', 0, '2008-07-08', 0, 2, 2, 0),
(7, 'Hạ trắng', 'Gọi nắng trên vai em gầy đường xa áo bay <br />\r\nNắng qua mắt buồn , lòng hoa bướm   say <br />\r\nLối em đi về trời không có mây <br />\r\nĐường đi suốt mùa nắng lên thắp đầy <br />\r\n<br />\r\nGọi nắng cho cơn mê chiều nhiều hoa trắng bay <br />\r\nCho tay em dài gầy   thêm nắng mai <br />\r\nBước chân em về nào anh có hay <br />\r\nGọi em cho nắng chết trên   sông dài.<br />\r\n<br />\r\nThôi xin ơn đời trong cơn mê này gọi mùa thu tới <br />\r\nTôi đưa em   về chân em bước nhẹ trời buồn gió cao <br />\r\nĐời xin có nhau dài cho mãi sau nắng   không gọi sầu <br />\r\nÁo xưa dù nhàu cũng xin bạc đầu gọi mãi tên nhau <br />\r\n<br />\r\nGọi   nắng cho tóc em cài loài hoa nắng rơi<br />\r\nNắng đưa em về miền cao gió bay<br />\r\nÁo   em bây giờ mờ xa nẻo mây<br />\r\nGọi tên em mãi suốt cơn mê này.\r\n', '2/Hatrang-KhanhLy.mp3', 3, '2008-07-09', 2, 2, 2, 0),
(8, 'Hoa vàng mấy độ', '<p>Em đến bên đời hoa vàng một đóa<br />\r\n  một thoáng hương bay bên trời   phố hạ<br />\r\n  nào có ai hay ta gặp tình cờ<br />\r\n  nhưng là cơn gió em còn cứ mãi bay   đi</p>\r\n<p>em đến bên đời hoa vàng rực rỡ<br />\r\n  nào dễ chóng phai trong lòng   nỗi nhớ<br />\r\n  ngày tháng trôi qua cơn đau mịt mù</p>\r\n<p>xin cho bốn mùa<br />\r\n  đất trời lặng gió<br />\r\n  đường trần em đi<br />\r\n  hoa   vàng mấy độ<br />\r\n  những đường cỏ lá<br />\r\n  từng giọt sương thu<br />\r\n  yêu em thật thà</p>\r\n<p>em đến nơi này bao điều chưa nói<br />\r\n  lặng lẽ chia xa sao lòng quá   vội<br />\r\n  một cõi bao la ta về ngậm ngùi<br />\r\n  em cười đâu đó trong lòng phố xá đông   vui</p>\r\n<p>em đến nơi này vui buồn đi nhé<br />\r\n  đời sẽ trôi xuôi qua ghềnh qua   suối<br />\r\n  một vết thương thôi riêng cho một người</p>\r\n', '', 0, '2008-07-09', 0, 2, 2, 1),
(9, 'Một cõi đi về', '<p>Bao nhiêu năm rồi còn mãi ra đi <br />\r\n  Đi đâu loanh quanh cho đời   mỏi mệt <br />\r\n  Trên hai vai ta đôi vầng nhật nguyệt <br />\r\n  Rọi suốt trăm năm một cõi   đi về <br />\r\n  Lời nào của cây lời nào cỏ lạ <br />\r\n  Một chiều ngồi say một đời thật nhẹ   ngày qua <br />\r\n  Vừa tàn mùa xuân rồi tàn mùa hạ <br />\r\n  Một ngày đầu thu nghe chân ngựa   về chốn xa <br />\r\n  Mây che trên đầu và nắng trên vai <br />\r\n  Đôi chân ta đi sông còn ở   lại <br />\r\n  Con tinh yêu thương vô tình chợt gọi <br />\r\n  Lại thấy trong ta hiện bóng con   người <br />\r\n  Nghe mưa nơi nầy lại nhớ mưa xa <br />\r\n  Mưa bay trong ta bay từng hạt nhỏ <br />\r\n  Trăm năm vô biên chưa từng hội ngộ <br />\r\n  Chẳng biết nơi nao là chốn quê nhà <br />\r\n  Đường chạy vòng quanh một vòng tiều tụy <br />\r\n  Một bờ cỏ non một bờ mộng mị   ngày xưa <br />\r\n  Từng lời tà dương là lời mộ địa <br />\r\n  Từng lời bể sông nghe ra từ độ   suối khe <br />\r\n  Trong khi ta về lại nhớ ta đi <br />\r\n  Đi lên non cao đi về biển rộng <br />\r\n  Đôi tay nhân gian chưa từng độ lượng <br />\r\n  Ngọn gió hoang vu thổi suốt xuân   thì <br />\r\n  Hôm nay ta say ôm đời ngủ muộn <br />\r\n  Để sớm mai đây lại tiếc xuân thì</p>\r\n', '2/MotCoiDiVe.mp3', 0, '2008-07-10', 0, 2, 2, 0),
(10, 'Ru tình', '<div>Ru em đầu cơn   gió, em hong tóc bên hồ <br />\r\n  Khi sen hồng mới nở, nụ đời ôi thơm quá <br />\r\n  Ru em   tình khi nhớ, ru em tình lúc xa <br />\r\n  Ru cho bầy lá nhỏ, rụng đầy một mùa Thu <br />\r\n  <br />\r\n  Ru khi mùa mưa tới, ru em mãi yêu người <br />\r\n  Ru em hoài bé dại, một hồn   thơm cây trái <br />\r\n  Ru em chờ em nói, trên môi tình thoát thai <br />\r\n  Ru em ngồi yên   đấy, ru tình à ..ơi <br />\r\n  <br />\r\n  Ru người ngồi mãi cùng tôi <br />\r\n  Ru người ngồi mãi   cùng tôi <br />\r\n  <br />\r\n  Ru em hài nhung gấm, ru em gót sen hồng <br />\r\n  Ru bay tà áo rộng,   vượt tình tôi chấp cánh <br />\r\n  Ru trên đường em đến, xôn xao từng tiếng chim <br />\r\n  Ru   em là cánh nhạn, miệng ngọt hạt từ tâm <br />\r\n  <br />\r\n  Ru em tình như lá, trăm năm vẫn   quay về <br />\r\n  Môi em là đốm lửa, cuộc đời đâu biết thế <br />\r\n  Xin em còn đâu đó, cho   tôi còn tiếng ru <br />\r\n  Ru em ngồi yên đấy, tôi tìm cuộc tình cho</div>\r\n', '', 0, '2008-07-15', 0, 2, 2, 0),
(11, 'Tình xa', '', '', 0, '2008-07-16', 0, 2, 2, 1),
(12, 'Tình nhớ', '<p>Tình ngỡ đã quên đi như lòng cố lạnh lùng <br />\r\n  Người ngỡ đã xa   xăm bỗng về quá thênh thang <br />\r\n  Ôi áo xưa lồng lộng đã xô dạt trời chiều <br />\r\n  Như   từng cơn nước rộng xóa một ngày đìu hiu </p>\r\n<p>Tình ngỡ đã phôi pha nhưng tình vẫn còn đầy <br />\r\n  Người ngỡ đã đi   xa nhưng người vẫn quanh đây <br />\r\n  Những bước chân mềm mại đã đi vào đời người <br />\r\n  Như từng viên đá cuội rớt vào lòng biển khơi </p>\r\n<p>Khi cơn đau chưa dài thì tình như chút nắng <br />\r\n  Khi cơn đau lên   đầy thì tình đã mênh mông <br />\r\n  Một người về đỉnh cao, một người về vực sâu <br />\r\n  Để   cuộc tình chìm mau như bóng chim cuối đèo </p>\r\n<p>Tình ngỡ chết trong nhau nhưng tình vẫn rộn ràng <br />\r\n  Người ngỡ   đã quên lâu nhưng người vẫn bâng khuâng <br />\r\n  Những ngón tay ngại ngùng đã ru lại   tình gần <br />\r\n  Như ngoài khơi gió động hết cuộc đời lênh đênh </p>\r\n<p>Người ngỡ đã xa xưa nhưng người bỗng lại về <br />\r\n  Tình ngỡ sóng xa   đưa nhưng còn quá bao la <br />\r\n  Ôi trái tim phiền muộn đã vui lại một giờ <br />\r\n  Như   bờ xa nước cạn đã chìm vào cơn mưa. <br />\r\n</p>\r\n', '', 0, '2008-07-16', 0, 2, 2, 0),
(13, 'Ướt mi', '<p>Ngoài hiên mưa rơi rơi.Lòng ai như chơi vơi <br />\r\n  Người ơi nước   mắt hoen mi rồi <br />\r\n  Đừng khóc trong đêm mưa.Đừng than trong câu ca<br />\r\n  Buồn ơi   trong đêm thâu. Ôm ấp giùm ta nhé <br />\r\n  Người em thương mưa ngâu hay khóc sầu nhân   thế <br />\r\n  Tình ta đêm về có ấm từng cơn mơ em chưa.<br />\r\n  <br />\r\n  Mưa lạnh lùng rơi rớt   giữa đêm về<br />\r\n  Nghe não nề<br />\r\n  Mưa kéo dài lê thê những đêm khuya <br />\r\n  Lạnh ướt mi <br />\r\n  Ai còn nhìn mưa mãi rớt bên sông thêm lạnh lùng<br />\r\n  Ai còn buồn khi lá rớt   rơi trong một cuối đông<br />\r\n  <br />\r\n  Ngoài hiên mưa rơi rơi. Buồn dâng lên đôi môi <br />\r\n  Buồn đau hoen ướt mi ai rồi <br />\r\n  Buồn đi trong đêm khuya. Buồn rơi theo đêm   mưa <br />\r\n  Còn mưa trong đêm nay, lòng em buồn biết mấy <br />\r\n  Trời sao chưa thôi mưa   để mắt người em ấy <br />\r\n  Từ đây thôi mờ, nước mắt buồn mi em ngây thơ</p>\r\n', '', 0, '2008-07-16', 0, 2, 2, 0),
(14, 'Ru đời đi nhé', '<div>Có khi mưa   ngoài trời <br />\r\n  là giọt nước mắt em. <br />\r\n  Đã nương theo vào đời <br />\r\n  làm từng nỗi   ưu phiền... <br />\r\n  <br />\r\n  Ngoài phố mùa Đông <br />\r\n  đôi môi em là đốm lửa hồng. <br />\r\n  <br />\r\n  Ru đời đi nhé <br />\r\n  cho ta nương nhờ lúc thở than <br />\r\n  Chân đi nằng nặng <br />\r\n  hoang mang ta nghe tịch lặng <br />\r\n  rơi nhanh dưới khe im lìm. <br />\r\n  <br />\r\n  Ru đời   đi nhé <br />\r\n  ôi môi ngon này giữa trần gian. <br />\r\n  Ru từng chiếc bóng <br />\r\n  lênh đênh   vào giấc ngủ ngon. <br />\r\n  Cho tôi tay gối mong manh, <br />\r\n  cho tôi ôm lấy vai   thon</div>\r\n', '', 0, '2008-07-16', 0, 2, 2, 0),
(15, 'Ru ta ngậm ngùi', '<p>Môi nào hãy còn thơm, cho ta phơi cuộc tình.<br />\r\n  Tóc nào hãy còn   xanh, cho ta chút hồn nhiên<br />\r\n  Tim nào có bình yên, ta rêu rao đời mình<br />\r\n  Xin   người hãy gọi tên.</p>\r\n<p>Khi tình đã vội quên, tim lăn trên đường mòn<br />\r\n  Trên giọt máu   cuồng điên, con chim đứng lặng câm<br />\r\n  Khi về trong mùa đông, tay rong rêu muộn   màng.<br />\r\n  Thôi chờ những rạng đông..</p>\r\n<p>Xin chờ những rạng đông<br />\r\n  Đời sao im vắng<br />\r\n  Như đồng lúa gặt   xong<br />\r\n  Như rừng núi bỏ hoang<br />\r\n  Người về soi bóng mình.<br />\r\n  Giữa tường trắng   lặng câm.</p>\r\n<p>Có đường phố nào vui, cho ta qua một ngày<br />\r\n  Có sợi tóc nào bay,   trong trí nhớ nhỏ nhoi<br />\r\n  Không còn, không còn ai, ta trôi trong cuộc   đời<br />\r\n  Không chờ, không chờ aị</p>\r\n<p>Em về, hãy về đi, ta phiêu du một đờị<br />\r\n  Hương trầm có còn đây,   ta thắp nốt chiều nay<br />\r\n  Xin ngủ trong vòng nôi, ta ru ta ngậm ngùi,<br />\r\n  Xin ngủ   dưới vòm cây ... </p>\r\n', '', 0, '2008-07-17', 0, 2, 2, 0),
(16, 'Còn ai với ai', '', '', 0, '2008-07-15', 0, 2, 2, 0),
(17, 'Xin trả nợ người', 'Hai mươi năm xin trả nợ người <br />\r\n<br />\r\n<br />\r\nTrả nợ một thời em đã bỏ ai <br />\r\nHai   mươi năm xin trả nợ dài <br />\r\nTrả nợ một đời em đã phụ tôi <br />\r\n<br />\r\nEm phụ tôi một   đời bé dại <br />\r\nThơ dại ra đi không nhớ gì tôi <br />\r\n<br />\r\n<br />\r\nThơ dại ra đi quên hết   tình tôi <br />\r\n<br />\r\nHai mươi năm em trả lại rồi <br />\r\nTrả nợ một đời xa vắng vòng tay <br />\r\nHai mươi năm vơi cạn lại đầy <br />\r\nTrả nợ một thời môi <br />\r\n<br />\r\nvắng vòng môi. <br />\r\n<br />\r\nBao nhiêu năm em nợ ngọt ngào <br />\r\nTrả nợ một đời chưa hết tình sâu <br />\r\nBao nhiêu năm em nợ bạc đầu <br />\r\nTrả nợ một đời không hết tình đâu <br />\r\n<br />\r\n<br />\r\n<br />\r\nEm phụ tôi một đời bé dại <br />\r\nThơ dại ra đi không nhớ gì tôi <br />\r\nThơ dại ra đi quên hết tình tôi <br />\r\n<br />\r\nBao nhiêu năm bỗng lại nhiệm mầu <br />\r\nTrả nợ một <br />\r\n<br />\r\nlần quên hết tình đau <br />\r\nHai mươi năm vẫn là thuở nào <br />\r\nNợ lại lần này trong cõi đời nhau.\r\n', '', 0, '2008-07-21', 0, 2, 2, 0),
(18, 'Như cánh vạc bay', '<p>Nắng có hồng bằng đôi môi em <br />\r\n  Mưa có buồn bằng đôi mắt em <br />\r\n  Tóc em từng sợi nhỏ <br />\r\n  Rớt xuống đời làm sóng lênh đênh <br />\r\n  <br />\r\n  Gió sẽ mừng   vì tóc em bay <br />\r\n  Cho mây hờn ngủ trên trên vai <br />\r\n  Vai em gầy guộc nhỏ <br />\r\n  Như   cánh vạc về chốn xa xôi <br />\r\n  <br />\r\n  Nắng có còn hờn ghen môi em <br />\r\n  Mưa có còn buồn   trong mắt em <br />\r\n  Từ lúc đưa em về <br />\r\n  Là biết xa nghìn trùng <br />\r\n  <br />\r\n  Suối đón   từng bàn chân em qua <br />\r\n  Lá hát từ bàn tay thơm tho <br />\r\n  Lá khô vì đợi chờ <br />\r\n  Cũng như đời người mãi âm u <br />\r\n  <br />\r\n  Nơi em về ngày vui không em <br />\r\n  Nơi em   về trời xanh không em <br />\r\n  Ta nghe từng giọt lệ <br />\r\n  Rớt xuống thành hồ nước long   lanh</p>\r\n', '2/HongNhung-NhuCanhVacBay.mp3', 5, '2008-08-09', 1, 2, 2, 20),
(19, 'Ru em', '<div>Ru em ngủ   những đêm khuya<br />\r\n  Ru em ngủ tháng âm u<br />\r\n  Ru em cùng những u mê<br />\r\n  Ru em, ru em   dù đã chia xa<br />\r\n  <br />\r\n  Ru em về những đêm xưa<br />\r\n  Ru em phụ rẩy trong ta<br />\r\n  Ru em   quì gối vong nô<br />\r\n  Ru em, ru em vì dáng kiêu sa<br />\r\n  <br />\r\n  Ru từng ngọt bùi đã   qua<br />\r\n  Ru người lận đận héo khô<br />\r\n  Yêu em, yêu thêm tình phụ<br />\r\n  Yêu em lòng chợt   từ bi bất ngờ<br />\r\n  <br />\r\n  Ru em thèm khát xa hoa<br />\r\n  Ru em đầy những đam mê<br />\r\n  Ru em   tình nghĩa vu vơ<br />\r\n  Ru em, ru em chìm dưới phong ba<br />\r\n  <br />\r\n  Ru em mệt lã cơn   đau<br />\r\n  Ru em về giữa chiêm bao<br />\r\n  Ru em bồng bế con theo<br />\r\n  Ru em, ru em gầy yếu   hư hao<br />\r\n  <br />\r\n  Thôi rồi còn gì nữa đâu<br />\r\n  Đã tàn mộng mị khát khao<br />\r\n  Đôi khi con   tim hò hẹn<br />\r\n  Ngậm ngùi vì một ngày mưa bắt đầu ...<br />\r\n</div>\r\n', '2/HongNhung-RuEm.mp3', 5, '2008-09-04', 0, 2, 2, 0),
(20, 'Cho đời chút ơn', '<p>Hôm chợt thấy em đi về bên kia phố<br />\r\n  Trong lòng bỗng vui như   đời rất lạ<br />\r\n  Tôi tìm thấy tôi theo từng gót xa<br />\r\n  Làm lời hát ca trên đường   đi<br />\r\n  Tôi tìm thấy tôi như giọt nắng kia<br />\r\n  Làm hồng chút môi cho em nhờ</p>\r\n<p>Môi thiên đường hót chim khuyên<br />\r\n  Ôi tóc trầm ướp vai   thơm<br />\r\n  Ta nghe đời rất mênh mông<br />\r\n  Trong chân người bước chậm chậm.</p>\r\n<p>Hãy còn bước đi cho bình minh lên sớm<br />\r\n  Cho đời chút ơn biết tà   áo nọ<br />\r\n  Em là phấn thơm cho rừng chút hương<br />\r\n  Làm lời hát ca cho trần   gian<br />\r\n  Dưới đường phố kia có người nhớ em<br />\r\n  Nằm mộng suốt đêm trong thiên   đường</p>\r\n', '', 0, '2008-09-07', 0, 2, 2, 0),
(21, 'Cỏ xót xa đưa', '<div>Trên đời   người trổ nhánh hoang vu <br />\r\n  Trên ngày đi mọc cành lá mù <br />\r\n  Những tim đời đập   lời hoang phế. <br />\r\n  <br />\r\n  Dưới mặt trời ngồi hát hôn mê <br />\r\n  Dưới vòng nôi mọc từng   nấm mộ <br />\r\n  Dưới chân ngày cỏ xót xa đưa. <br />\r\n  <br />\r\n  Người đã đến và người sẽ về bên   kia núi <br />\r\n  Từng câu nói là từng cánh buồm giong cuối trời. <br />\r\n  Còn lại tiếng   cười khóc giữa đời. <br />\r\n  <br />\r\n  Dưới ngọn đèn một bóng chim qua <br />\r\n  Giữa đường đi   một người đứng gọi <br />\r\n  Có biết gì về ngày chưa tới. <br />\r\n  Những ngày ngồi rủ tóc   âm u <br />\r\n  Nghe tiền thần về chào bóng lạ <br />\r\n  Những mai hồng ngồi nhớ thiên   thu</div>\r\n', '2/HongNhung-CoXotXaDua.mp3', 5, '2008-09-05', 0, 2, 2, 0),
(22, 'Đêm thấy ta là thác đổ', '<p>Một đêm bước chân về gác nhỏ <br />\r\n  Chợt nhớ đóa hoa Tường Vi <br />\r\n  Bàn tay ngắt hoa từ phố nọ <br />\r\n  Giờ đây đã quên vườn xưa <br />\r\n  Một hôm bước qua   thành phố lạ <br />\r\n  Thành phố đã đi ngủ trưa <br />\r\n  Đời ta có khi tựa lá cỏ <br />\r\n  Ngồi   hát ca rất tự do <br />\r\n  Nhiều khi bỗng như trẻ nhớ nhà <br />\r\n  Từ những phố xưa tôi về <br />\r\n  Ngày xuân bước chân người rất nhẹ <br />\r\n  Mùa xuân đã qua bao giờ <br />\r\n  Nhiều đêm   thấy ta là thác đổ <br />\r\n  Tỉnh ra có khi còn nghe </p>\r\n<p>Một hôm bước chân về giữa chợ <br />\r\n  Chợt thấy vui như trẻ thơ <br />\r\n  Đời ta có khi là đóm lửa <br />\r\n  Một hôm nhóm trong vườn khuya <br />\r\n  Vườn khuya   đóa hoa nào mới nở <br />\r\n  Đời tôi có ai vừa qua <br />\r\n  Nhiều khi thấy trăm nghìn nấm   mộ <br />\r\n  Tôi nghĩ quanh đây hồ như <br />\r\n  Đời ta hết mang điều mới lạ <br />\r\n  Tôi đã sống   rất ơ hờ <br />\r\n  Lòng tôi có đôi lần khép cửa <br />\r\n  Rồi bên vết thương tôi quì <br />\r\n  Vì   em đã mang lời khấn nhỏ <br />\r\n  Bỏ tôi đứng bên đời kia </p>\r\n', '2/DemThayTalaThacDo.mp3', 0, '2008-07-16', 0, 2, 2, 0),
(23, 'Tuổi đá buồn', '<p>Trời còn làm mưa<br />\r\n  Mưa rơi mênh mang <br />\r\n  Từng ngón tay   buồn<br />\r\n  Em mang em mang <br />\r\n  Đi về giáo đường ngày chủ nhật buồn <br />\r\n  Còn ai còn   ai <br />\r\n  Đóa hoa hồng cài lên tóc mây <br />\r\n  Ôi đường phố dài <br />\r\n  Lời ru miệt mài <br />\r\n  Ngàn năm ngàn năm <br />\r\n  Ru em nồng nàn <br />\r\n  Ru em nồng nàn</p>\r\n<p>Trời còn làm mây <br />\r\n  Mây trôi lang thang <br />\r\n  Sợi tóc em bồng <br />\r\n  Trôi nhanh trôi nhanh <br />\r\n  Như giòng nước hiền <br />\r\n  Ngày chủ nhật buồn <br />\r\n  Còn   ai còn ai <br />\r\n  Đóa hoa hồng vùi quên trong tay <br />\r\n  Ôi đường phố dài <br />\r\n  Lời ru   miệt mài <br />\r\n  Ngàn năm ngàn năm <br />\r\n  Ru em giận hờn <br />\r\n  Ru em giận hờn </p>\r\n<p>Trời còn làm mưa <br />\r\n  Mưa rơi mưa rơi <br />\r\n  Từng phiến băng dài <br />\r\n  Trên hai tay xuôi <br />\r\n  Tuổi buồn em mang <br />\r\n  Đi trong hư vô <br />\r\n  Ngày qua hững   hờ <br />\r\n  Trời còn làm mưa <br />\r\n  Mưa rơi mưa rơi <br />\r\n  Từng phiến mây hồng <br />\r\n  Em mang   trên vai <br />\r\n  Tuổi buồn như lá <br />\r\n  Gió mãi cuốn đi <br />\r\n  Quay tận cuối trời </p>\r\n<p>Trời còn làm mưa <br />\r\n  Mưa rơi thênh thang <br />\r\n  Từng gót chân trần <br />\r\n  Em quên em quên <br />\r\n  Ôi miền giáo đường <br />\r\n  Ngày chủ nhật buồn <br />\r\n  Còn ai còn   ai <br />\r\n  Đóa hoa hồng tàn hôn lên môi <br />\r\n  Em gầy ngón dài <br />\r\n  Lời ru miệt mài <br />\r\n  Ngàn năm ngàn năm <br />\r\n  Ru em muộn phiền <br />\r\n  Ru em bạc lòng</p>\r\n', '', 0, '2008-07-15', 0, 2, 2, 0),
(24, 'Trong nỗi đau tình cờ', '<p>Tôi đã yêu em bao ngày nắng <br />\r\n  Tôi đã yêu em bao ngày mưa <br />\r\n  Yêu em bên đời lặng lẽ <br />\r\n  Tôi đã đưa em qua nhiều phố <br />\r\n  Những sáng mênh   mông tôi ngồi nhớ <br />\r\n  Yêu em trái tim thật thà <br />\r\n  <br />\r\n  Yêu đầy mùa nắng mùa mưa <br />\r\n  Yêu trong nỗi vui đợi chờ <br />\r\n  Đâu ngờ tình như lá úa <br />\r\n  Khiến tôi chia lìa   từng giấc mơ <br />\r\n  <br />\r\n  Tôi đã yêu em trong mùa gió <br />\r\n  Khi lá cây khô bay đầy ngõ <br />\r\n  Yêu em không cần vội vã <br />\r\n  Tôi đã yêu em như trẻ thơ <br />\r\n  Đâu biết đôi khi   có lìa xa <br />\r\n  Yêu trong nỗi đau tình cờ</p>\r\n', '', 0, '2008-07-14', 0, 2, 2, 0),
(25, 'Em đến từ nghìn xưa', '<div>Tôi vẫn nhìn   thấy em <br />\r\n  Giữa đám đông xa lạ <br />\r\n  Vì em mang trong mắt <br />\r\n  Nỗi yêu đời thiết   tha <br />\r\n  Tôi vẫn nhìn thấy em <br />\r\n  Giữa đám dông xa lạ <br />\r\n  Vì trong đôi mắt đó <br />\r\n  Có quê hương bạn bè <br />\r\n  <br />\r\n  Em đã đến giữa quê hương <br />\r\n  Có những nghìn năm   xưa <br />\r\n  Hoá thân em bây giờ <br />\r\n  Nên tôi vẫn nhìn thấy em <br />\r\n  Giữa đám đông xa lạ <br />\r\n  Vì em như chim trắng <br />\r\n  Giữa trống đồng bước ra <br />\r\n  Tôi vẫn nhìn thấy em <br />\r\n  Giữa đám đông xa lạ <br />\r\n  Vì em như hoa lá <br />\r\n  Giữa thiên nhiên hiền hòa</div>\r\n', '2/Emdentunghinxua-ThanhThuy.mp3', 0, '2008-09-04', 0, 2, 2, 0),
(26, 'Tôi ơi đừng tuyệt vọng', '', '', 0, '2008-09-06', 0, 2, 2, 0),
(27, 'Con mắt còn lại', 'Còn hai con mắt khóc người một con. <br />\r\nCòn hai con mắt một con khóc   người. <br />\r\nCon mắt còn lại nhìn cuộc đời tôi. <br />\r\nNhìn tôi lên cao nhìn tôi   xuống thấp. <br />\r\nCon mắt còn lại nhìn cuộc tình \r\nphai. <br />\r\nTình trong hai   tay một hôm biến mất. <br />\r\nCon mắt còn lại là con mắt ai. <br />\r\nCon mắt còn lại   nhìn tôi thở dài. <br />\r\nCòn hai con mắt khóc người một con \r\n<br />\r\n<br />\r\nCòn hai con   mắt một con khóc người. <br />\r\nCon mắt còn lại nhìn một thành hai <br />\r\nNhìn em yêu   thương nhìn em thú dữ. <br />\r\nCon mắt còn lại ngờ vực tình tôi. \r\n<br />\r\n<br />\r\nCuồng   điên yêu thương cuồng điên nỗi nhớ <br />\r\nCon mắt còn lại nhìn mây trắng bay <br />\r\nCon mắt còn lại nhìn tôi bùi ngùi. <br />\r\nCòn hai con mắt khóc người một con \r\n<br />\r\n<br />\r\nCòn hai con mắt một con khóc người. <br />\r\nCon mắt còn lại nhìn đời là   không <br />\r\nNhìn em hư vô nhìn em bóng nắng <br />\r\nCon mắt còn lại nhẹ nhàng từ tâm. <br />\r\nNhìn \r\nem ra đi lòng em xa vắng. <br />\r\nCon mắt còn lại là đêm tối tăm <br />\r\nCon mắt còn lại là đêm nồng nàn.\r\n', '2/Conmatconlai-TrinhCongSon.mp3', 7, '2008-09-05', 0, 2, 2, 0),
(28, 'Sóng về đâu', '<div>Biển sóng   biển sóng đừng xô tôi<br />\r\n  Đừng xô tôi ngã dưới chân người<br />\r\n  Biển sóng biển sóng   đừng xô nhau<br />\r\n  Ta xô biển lại sóng về đâu?<br />\r\n  <br />\r\n  Sóng bạc đầu và núi chìm   sâu<br />\r\n  Ta về đâu đó<br />\r\n  Về chốn nào mây phủ chiêm bao<br />\r\n  Cạn suối nguồn bốn bề   nương dâu<br />\r\n  Ta tìm em nơi đâu?<br />\r\n  <br />\r\n  Biển sóng biển đừng xô nhau<br />\r\n  Ta xô biển   lại sóng nằm đau<br />\r\n  Biển sóng biển sóng đừng xô tôi<br />\r\n  Đừng xô ngã tôi giữa tim   người.<br />\r\n  <br />\r\n  Biển sóng biển sóng đừng xô tôi<br />\r\n  Đừng cho tôi thấy hết tim   người<br />\r\n  Biển sóng biển sóng đừng xô nhau<br />\r\n  Ta xô biển lại sóng về   đâu?<br />\r\n  <br />\r\n  Giấc ngủ nào giường chiếu quạnh hiu<br />\r\n  Trăng mờ quê cũ<br />\r\n  Người đứng   chờ gió đông<br />\r\n  Vạt nắng vàng nhắc lời thiên thu<br />\r\n  Nhớ ngàn năm trôi   qua.<br />\r\n  <br />\r\n  Biển sóng biển sóng đừng trôi xa<br />\r\n  Bao năm chờ đợi sóng gần   ta<br />\r\n  Biển sóng biển sóng đừng âm u<br />\r\n  Đừng nuôi trong ấy trái tim   thù.<br />\r\n  <br />\r\n  Biển sóng... biển sóng... đừng xô nhau.</div>\r\n', '', 0, '2008-10-01', 0, 2, 2, 0),
(29, 'Em đi bỏ lại con đường', '<p>Bỏ mặc căn nhà, bỏ mặc tôi<br />\r\n  Bỏ mặc nơi đây, bỏ mặc người<br />\r\n  Bỏ   trăm năm sau, ngàn năm nữa<br />\r\n  Bỏ mặc tôi là, tôi là ai</p>\r\n<p>Em đi bỏ lại con đường<br />\r\n  Bờ xa cỏ dại, vô thường nhớ em<br />\r\n  Ra   đi,em đi bỏ lại dậm trường<br />\r\n  Ngàn dâu cố quạnh, muôn trùng nhớ thêm</p>\r\n<p>Bỏ mặc đêm dài, bỏ mặc tôi<br />\r\n  Bỏ mặc gian nan, bỏ mặc   người<br />\r\n  Bỏ xa xôi yêu và gần gũi<br />\r\n  Bỏ mặc tôi buồn giữa cuộc vui</p>\r\n<p>Bỏ mặc mưa về, bỏ chiều phai<br />\r\n  Bỏ mặc hư vô, bỏ ngậm ngùi<br />\r\n  Bỏ   đêm chưa qua, ngày chưa tới<br />\r\n  Bỏ mặc tay buồn không bàn tay</p>\r\n<p>Bỏ mặc vui buồn, bỏ mặc ai<br />\r\n  Bỏ mặc chân không, bỏ mặc   người<br />\r\n  Bỏ tôi hoang vu và nhỏ bé<br />\r\n  Bỏ mặc tôi ngồi giữa đời tôi</p>\r\n', '', 0, '2008-09-02', 0, 2, 2, 0),
(30, 'Bèo giạt mây trôi', 'Bèo dạt mây trôi, chốn xa xôi,<br />\r\nAnh   ơi, em vẫn đợi... í i ì... bèo dạt<br />\r\nMây í i ì... trôi,<br />\r\nchim sa, tang tính   tình... í i ì..., cao vời<br />\r\nNgậm một tin trông, hai tin đợi, ba bốn tin chờ<br />\r\nsao   chẳng thấy anh...<br />\r\n<br />\r\nMột mảnh trăng treo, suốt năm canh,<br />\r\nAnh ơi, trăng đã   ngã... a á à... ngang đầu<br />\r\nThương nhớ... ờ ơ... ai, sao rơi... đêm sắp tàn...   í i ì...<br />\r\ntrăng tà,<br />\r\nNgười ra đi có nhớ, (hay người xa có nhớ)<br />\r\nlà nhớ ai   ngồi trông cánh chim trời,<br />\r\nsao chẳng thấy anh...<br />\r\n<br />\r\nMòn mỏi thâu đêm,   suốt năm canh<br />\r\nAnh ơi, em vẫn đợi... í i ì... mỏi   mòn<br />\r\nThương nhớ... ờ ơ... ai<br />\r\nSao rơi, trăng sắp tàn .. í i ì... trăng   tà<br />\r\nCành tre đu trước ngõ<br />\r\nLà gió la đà em vẫn mong chờ, sao chẳng thấy   anh...<br />\r\n<br />\r\nThương nhớ.. ờ ơ... ai<br />\r\nSao rơi, trăng sắp tàn .. í i ì... trăng   tà<br />\r\nCành tre đu trước ngõ<br />\r\nLà gió la đà em vẫn mong chờ, sao chẳng thấy   anh....\r\n', '1/Beodatmaytroi-ThuHien.mp3', 11, '2008-09-01', 2, 1, 1, 42),
(31, 'Bông bí vàng', '<p>Bông bí vàng ngoài giàn, công em trồng anh không hái<br />\r\n  Trời sa   mưa bông kết trái, từ đó buồn, em buồn.<br />\r\n  Hum hum hum .... hum<br />\r\n  Trời mưa trên   khóm mía lau<br />\r\n  Trời rụng giọt mưa, xao xuyến xuyến xao<br />\r\n  Nhà em, dậu đổ bìm   leo<br />\r\n  Mà nhà anh mẹ cha rào mấy lượt rào<br />\r\n  Hỏi chớ con đường mòn nào, mà hai   đứa để gặp nhau<br />\r\n  Xui nên ta làm việc nhiều<br />\r\n  Kiếm đồng tiền dành mua sắm trầu   cau.<br />\r\n  Trâu cày ruộng cạn, trâu thở liền vô con trâu ơi thương dùm cuộc tình họ   đang cần có đôi bạn làm ăn<br />\r\n  Hum hum hum .... hum.<br />\r\n  Bây giờ trầu xanh vườn   trầu, cau trổ quày cau<br />\r\n  Mà mẹ cha không ngó xuống!<br />\r\n  Gió thổi năm non mà mẹ   cha chưa mát trong lòng<br />\r\n  Hái bông bí anh trồng anh đem luộc cầu xin.<br />\r\n  Bây   giờ trời mưa ngập ruộng, thương muộn còn thương<br />\r\n  Cuộc tình duyên trôi theo   dòng nước<br />\r\n  Bí trổ bông thơm một giàn bông không muốn hái bông nào<br />\r\n  Nhớ   thương nửa cuộc tình sao nghe ruột quặn đau .</p>\r\n', '1/Bongbivang-PhiNhung.mp3', 12, '2008-09-01', 0, 3, 1, 22),
(32, 'Bông điên điển', 'Hò ơ! Má ơi đừng gả con xa, <br />\r\nChim \r\nkêu vượn hú.... Hò ơ! <br />\r\nChim kêu   vượn hú, Biết đâu má tìm!&quot; <br />\r\n<br />\r\nEm đi lấy chồng về nơi xứ xa, <br />\r\nĐêm ru điệu   hát câu hò trên môi. <br />\r\nMiền Tây xanh sắc mây trời, <br />\r\nPhù sa nước nổi   người ơi đừng về! <br />\r\nVới màu điên điển say mê, <br />\r\nvàng trong ánh mắt vỗ về gót   chân. <br />\r\nTrót thương tình nghĩa vợ chồng, <br />\r\nNên bông  điên điển nở cho   lòng vấn vương. <br />\r\nTình thương em khó mà lường. <br />\r\n<br />\r\nChồng gần không lấy, em   lấy chồng xa, <br />\r\nGiờ đây nhớ mẹ thương cha, <br />\r\nCòn đâu mà thong  thả đi   về nhà thăm. <br />\r\nXa xăm nơi chốn bưng biền, <br />\r\nĂn bông mà điên điển, <br />\r\nNghiêng mình nhớ đât quê. <br />\r\nChồng xa em khó mà về. <br />\r\nHò ơ, ơi hò! Chồng   xa em  khó mà dzề <br />\r\n', '1/BongDiendien-PhiNhung.mp3', 12, '2008-09-01', 0, 7, 1, 17),
(33, 'Bông bưởi hoa cau', '<p>Tóc con dài mẹ cài bông hoa lý<br />\r\n  Mẹ lượm từng bông bưởi trắng   con gội đầu<br />\r\n  Mẹ kể chuyện xưa bông bưởi hoa cau<br />\r\n  Mẹ dạy từng câu hò lý ca   dao</p>\r\n<p>Nắng thu buồn nhuộm vàng ngõ trúc vắng<br />\r\n  Từng nhịp võng đưa mẹ   hát tiếng nghe buồn<br />\r\n  Chừng lưng lửng cơm mẹ gói mo cau<br />\r\n  Mẹ lên ruộng sâu gặt   hái nuôi con</p>\r\n<p>Con chợt nhớ vườn cau trước cửa<br />\r\n  Rồi con chợt nhớ vườn bưởi   sau hè<br />\r\n  Mẹ không còn, lượm từng bông bưởi trắng<br />\r\n  Cơn nhẹ gió bông bưởi rụng   đầy sân</p>\r\n<p>Con sẽ nhớ vườn cau ấy mãi<br />\r\n  Và con sẽ nhớ vườn bưởi trọn   đời<br />\r\n  Để khi nào mùa thu rụng lá<br />\r\n  Con lại nhớ bông bưởi rụng đầy sân ...</p>\r\n<p>Bông bưởi thơm vườn bưởi<br />\r\n  Đâu nữa để gội đầu<br />\r\n  Nhớ câu hò   điệu lý<br />\r\n  Mang giai điệu lời ru</p>\r\n<p>Đừng bay .... mà mòn mỏi sức chim<br />\r\n  Rồi bơ vơ cảnh la.<br />\r\n  Sợ   con tìm về cây xưa<br />\r\n  Sợ con tìm ... con tìm về cây xưa, .... cây xưa<br />\r\n  Sợ con   tìm ... con tìm về cây xưa, .... cây xưa...</p>\r\n', '1/BongBuoiHoaCau.mp3', 0, '2008-09-02', 0, 3, 1, 5),
(34, 'Lý con sáo', '', '1/Lyconsao-PhiNhung.mp3', 12, '2008-09-02', 0, 1, 1, 4),
(36, 'Đường về hai thôn', 'Đường về thôn \r\nem duyên dáng bên ven sông con thuyền xuôi mái <br />\r\nNhịp   cầu băng qua men lối đi quanh co, cỏ hoa nối dài <br />\r\nNhà em cuối xóm ghép đôi   mái tranh nâu trăng cài \r\ntrước sau <br />\r\nCó tằm mến thương dâu, Có trầu vấn   vương cau <br />\r\nvà đào tơ thơm ngát, ngát hương trinh ban đầu <br />\r\nĐường về thôn   anh con sáo ru êm êm trên đồng xanh lúa <br />\r\nNhịp cầu băng qua đưa lối   sân rêu xưa thắm hoa bốn mùa <br />\r\nNhà anh mái lá tháng năm vẫn chưa nghe duyên   tròn ước mơ <br />\r\nCó giàn mướp xanh lơ, Có hồ cá nên \r\nthơ <br />\r\nmà lòng quê   vẫn hát hát bao câu mong chờ <br />\r\nMỗi đêm trăng thanh ngà hội mùa lên tiếng ca   góp tay cần lao <br />\r\nÔi hai thôn giao đầu nhịp cầu tre bước chung \r\nêm vui   dường bao <br />\r\nTình ta lên men rồi còn chi ngăn cách lòng mà chưa trao giấc mơ   xuân cùng nhau <br />\r\nChày dâng lên trăng màu, bà con đang mong cầu \r\nrằng \r\nđôi ta sớm nên duyên ban đầu <br />\r\nĐường về hai thôn mai mốt đôi uyên ương   qua cầu soi bóng <br />\r\nNhủ thầm sông ơi gương nước chưa phôi pha ta còn vui hoài <br />\r\nTrời \r\nquê bát ngát sẽ trông thấy tương lai qua tình lứa đôi <br />\r\nLúa   đồng mãi xanh tươi, mướp cà thắm nơi nơi <br />\r\nvà vành môi trai gái góp bao câu ca   yêu đời <br />\r\nNhịp cầu \r\nđưa lối, chung bước hai thôn ta đón trăng về mừng   duyên quê\r\n', '1/Duongvehaithon-ThuHien.mp3', 11, '2008-09-03', 0, 8, 1, 1),
